SELECT MIN(t.title) AS american_vhs_movie FROM company_type AS ct, info_type AS it, movie_companies AS mc, movie_info AS mi, title AS t WHERE t.id = mi.movie_id AND t.id = mc.movie_id AND mc.movie_id = mi.movie_id AND ct.id = mc.company_type_id AND it.id = mi.info_type_id AND ct.kind = 'miscellaneous companies' AND mc.note = '(2012) (USA) (theatrical)' AND mc.note = '(2012) (USA) (theatrical)' AND mc.note = '(2012) (USA) (theatrical)' AND mi.info = 'Australia' AND t.production_year < 2006