SELECT MIN(mi.info) AS budget, MIN(t.title) AS unsuccsessful_movie FROM company_name AS cn, company_type AS ct, info_type AS it1, info_type AS it2, movie_companies AS mc, movie_info AS mi, movie_info_idx AS mi_idx, title AS t WHERE t.id = mi.movie_id AND t.id = mi_idx.movie_id AND mi.info_type_id = it1.id AND mi_idx.info_type_id = it2.id AND t.id = mc.movie_id AND ct.id = mc.company_type_id AND cn.id = mc.company_id AND mc.movie_id = mi.movie_id AND mc.movie_id = mi_idx.movie_id AND mi.movie_id = mi_idx.movie_id AND cn.country_code = '[br]' AND ct.kind = 'special effects companies' AND ct.kind = 'special effects companies' AND ct.kind = 'special effects companies' AND it1.info = 'screenplay-teleplay' AND it2.info = 'screenplay-teleplay' AND t.production_year < 1978 AND t.title = 'Legacy of the Cannibal God' AND t.title = 'Legacy of the Cannibal God'