SELECT MIN(an.name) AS alternative_name, MIN(chn.name) AS voiced_character, MIN(n.name) AS voicing_actress, MIN(t.title) AS american_movie FROM aka_name AS an, char_name AS chn, cast_info AS ci, company_name AS cn, movie_companies AS mc, name AS n, role_type AS rt, title AS t WHERE ci.movie_id = t.id AND t.id = mc.movie_id AND ci.movie_id = mc.movie_id AND mc.company_id = cn.id AND ci.role_id = rt.id AND n.id = ci.person_id AND chn.id = ci.person_role_id AND an.person_id = n.id AND an.person_id = ci.person_id AND ci.note = '(as Brian Lucero)' AND cn.country_code = '[tr]' AND mc.note = '(2013) (Netherlands) (TV) (HBO3) (limited)' AND mc.note = '(2013) (Netherlands) (TV) (HBO3) (limited)' AND mc.note = '(2010) (Colombia) (TV)' AND n.gender = 'f' AND n.name = 'Pick-Crystal, Sasha' AND rt.role = 'editor' AND t.production_year < 1960